import React, { Component } from 'react';
import { Bar, Line, Pie } from 'react-chartjs-2';

class LineChart extends Component {

  constructor(props) {
    super(props);
    this.state = {
      bgColor: props.bgColor
    }

  }
  static defaultProps = {
    displayTitle: true,
    displayLegend: true,
    legendPosition: 'right'
  }

  async componentDidMount() {
    this.getChartData();
    const resp= await fetch('http://localhost:8080/survey/users?by=country')
        .then((response) => response.json())

    var countCountry= {};
    resp.array.forEach(element => {

    });

    console.log(resp)
  }

  getChartData() {
    this.setState({
      chartData: {
        labels: ['Boston', 'Worcester', 'Springfield', 'Lowell', 'Cambridge', 'New Bedford'],
        datasets: [
          {
            label: 'Duck Count',
            data: [
              617594,
              181045,
              153060,
              106519,
              105162,
              95072
            ],
            backgroundColor: [
              'rgba(255, 99, 132, 0.6)',
              'rgba(54, 162, 235, 0.6)',
              'rgba(255, 206, 86, 0.6)',
              'rgba(75, 192, 192, 0.6)',
              'rgba(153, 102, 255, 0.6)',
              'rgba(255, 159, 64, 0.6)',
              'rgba(255, 99, 132, 0.6)'
            ]
          }
        ]
      }
    })
  }

  render() {
    return (
      <>
        <Line
          data={this.state.chartData}
          width={600}
          height={150}
          options={{

            legend: {
              display: this.props.displayLegend,
              position: this.props.legendPosition,

              labels: {
                fontColor: this.state.bgColor,
                fontSize: 18
              }
            },
            scales: {
              yAxes: [{
                scaleLabel: {
                  display: true,
                  labelString: 'probability',
                  fontColor: this.state.bgColor
                },
                ticks: {
                  fontColor: this.state.bgColor,
                  fontSize: 20,


                }
              }],
              xAxes: [{
                ticks: {
                  fontColor: this.state.bgColor,
                  fontSize: 20,
                  stepSize: 1

                }
              }]
            }

          }}
        />
      </>
    );
  }
}

export default LineChart;
