import React,{useState} from 'react'

import { FeedCol } from '../Footer/FooterElements'
import { FeatureHeader, FeatureRow } from '../MainSection/FeaturesElements'
import { FeedForm, FormSection, Input, Select,FormHeader,Label } from './DuckFormElements'


const [displaytext,setDisplayTest] =useState(false)

const handleClick = (val) =>{ 
    if(val=='others'){
        setDisplayTest(true);
    }
    else{
        setDisplayTest(false);
    }
   }

function DuckForm() {

    return (
        <>
            <FormSection>
                <FeatureRow>
                    <FormHeader>
                        I welcome sharing your knowledge on ducks
                        </FormHeader>
                </FeatureRow>
                <FeatureRow>
                    <FeedForm method='post' action='#'>
                        <FeatureRow>
                            <FeedCol isValue={false}>
                                <Label for='name'>Nick Name</Label>

                            </FeedCol>
                            <FeedCol isValue={true}>
                                <Input type="text" name="name" id="name" placeholder="Your Nick name" required />
                            </FeedCol>

                        </FeatureRow>
                        <FeatureRow>
                            <FeedCol isValue={false}>
                                <Label for='park'>Park Name</Label>

                            </FeedCol>
                            <FeedCol isValue={true}>
                                <Input type="text" name="park" id="park" placeholder="Park Name" required />
                            </FeedCol>

                        </FeatureRow>


                        <FeatureRow>
                            <FeedCol isValue={false}>
                                <Label for='food'>Food</Label>

                            </FeedCol>
                            <FeedCol isValue={true}>
                                <Input type="text" name="food" id="food" placeholder="What food?" required />
                            </FeedCol>

                        </FeatureRow>
                        <FeatureRow>
                            <FeedCol isValue={false}>
                                <Label for='foodtype'>Food Type</Label>

                            </FeedCol>
                            <FeedCol isValue={true}>
                                <Select name="foodtype" id="foodtype" onChange={handleClick}>
                                    <option value="friends" selected>Grain</option>
                                    <option value="search">Fish</option>
                                    <option value="ad"> Plants</option>
                                    <option value="other">Worms</option>
                                </Select>
                            </FeedCol>

                        </FeatureRow>
                        <FeatureRow>
                            <FeedCol isValue={false}>
                                <Label for='food'>Food Type</Label>

                            </FeedCol>
                            <FeedCol isValue={true}>
                                <Input type="text" name="food_type_text" id="food" placeholder="What food?" required />
                            </FeedCol>

                        </FeatureRow>
                        <FeatureRow>
                            <FeedCol isValue={false}>
                                <Label for='park'>Time for feeding</Label>

                            </FeedCol>
                            <FeedCol isValue={true}>
                                <Input type="time" name="time" id="time" required />
                            </FeedCol>

                        </FeatureRow>
                        <FeatureRow>
                            <FeedCol isValue={false}>
                                <Label for='duck_count'>Duck Count</Label>

                            </FeedCol>
                            <FeedCol isValue={true}>
                                <Input type="text" name="duck_count" id="duck_count" placeholder="How Many Ducks are feed?" required />
                            </FeedCol>

                        </FeatureRow>
                        <FeatureRow>
                            <FeedCol isValue={false}>
                                <Label for='quantity'>Quantity</Label>

                            </FeedCol>
                            <FeedCol isValue={true}>
                                <Input type="text" name="quantity" id="quantity" placeholder="Quantity Feed?" required />
                            </FeedCol>

                        </FeatureRow>
                        <FeatureRow>
                            <FeedCol isValue={false}>
                                <Label></Label>

                            </FeedCol>
                            <FeedCol isValue={true}>
                                <Input type="submit" value='Submit' />
                            </FeedCol>

                        </FeatureRow>


                    </FeedForm>
                </FeatureRow>
            </FormSection>

        </>
    )
}

export default DuckForm




