import React from 'react'
import { useState, useEffect } from 'react'
import { Link } from 'react-router-dom'
import { IconContext } from 'react-icons/lib'
import { NavBar, NavContainer, NavigationLink, Times, Bars, NavIcon, NavMobile, NavMenu, NavButton, NavigationLinkToComponent } from './NavBarStyles';

function NavigationBar({isDashBoard=false}) {

    const [click, setClick] = useState(false);
    const [button, setButton] = useState(true);
    const handleClick = () => setClick(!click);
    const closeMobileMenu = () => setClick(false);
    const showButton = () => {
        if (window.innerWidth <= 960) {
            setButton(false)
        }
        else {
            setButton(true)
        }
    };

    useEffect(() => {
        showButton()

    }, [])
    window.addEventListener('resize', showButton)



    return (

        <>
            <NavBar>
                <NavContainer>
                    <NavigationLink to='header' smooth={true} duration={1000} islink={false} className={'nav-logo'} onClick={closeMobileMenu}>
                        <NavIcon />
                    Duck<strong>Feed</strong>
                    </NavigationLink>
                    <NavMenu className='menu-icon' onClick={handleClick}>
                        {
                            click ? <Times /> : <Bars />
                        }
                    </NavMenu>
                    {!isDashBoard ? (
                        <NavMobile>
                            <NavigationLink to='header'  islink={true} smooth={true} duration={1000} >Home</NavigationLink>
                            <NavigationLink to='features' key='2' islink={true} smooth={true} duration={1000} className={'nav-link'} onClick={closeMobileMenu}>Food</NavigationLink>
                            <NavigationLink to='survey' key='3' islink={true} smooth={true} duration={1000} className={'nav-link'} onClick={closeMobileMenu}>Survey</NavigationLink>
                            <NavigationLink to='about us' key='4' islink={true} smooth={true} duration={1000} className={'nav-link'} onClick={closeMobileMenu}>About Us</NavigationLink>
                            <NavigationLinkToComponent to='dashBoard' key='5' className={'nav-link'} onClick={closeMobileMenu}>
                                <NavButton isbutton={true}>
                                    DashBoard
                                                    {/* <NavigationLink isbutton={true} > DashBoard</NavigationLink> */}

                                </NavButton>
                            </NavigationLinkToComponent>
                        </NavMobile>
                        ) : (
                        <NavMobile className={'nav-menu'}>
                            <NavigationLinkToComponent to='home' key='1' islink={true} smooth={true} duration={1000} className={'nav-link'} onClick={closeMobileMenu}>Home</NavigationLinkToComponent>
                            <NavigationLink to='LineChart' key='2' islink={true} smooth={true} duration={1000} className={'nav-link'} onClick={closeMobileMenu}>Line</NavigationLink>
                            <NavigationLink to='BarChart' key='3' islink={true} smooth={true} duration={1000} className={'nav-link'} onClick={closeMobileMenu}>Bar</NavigationLink>
                            <NavigationLink to='PieChart' key='3' islink={true} smooth={true} duration={1000} className={'nav-link'} onClick={closeMobileMenu}>Pie</NavigationLink>
                            <NavigationLink to='Table' key='4' islink={true} smooth={true} duration={1000} className={'nav-link'} onClick={closeMobileMenu}>Table</NavigationLink>
                        </NavMobile>)
                    }






                </NavContainer>
            </NavBar>
        </>
    )
}

export default NavigationBar
