import React, { Component } from 'react';
import { Bar, Line, Pie } from 'react-chartjs-2';

class LineChart extends Component {

    constructor(props) {
        super(props);
        this.state = {
            chartData: props.chartData
        }
    }
    static defaultProps = {
        displayTitle: true,
        displayLegend: true,
        legendPosition: 'right'
    }

    componentWillMount() {
        this.getChartData();
    }

    getChartData() {
        this.setState({
            chartData: {
                labels: ['Boston', 'Worcester', 'Springfield', 'Lowell', 'Cambridge', 'New Bedford'],
                datasets: [
                    {
                        label: 'Duck Count',
                        data: [
                            617594,
                            181045,
                            153060,
                            106519,
                            105162,
                            95072
                        ],
                        backgroundColor: [
                            'rgba(255, 99, 132, 0.6)',
                            'rgba(54, 162, 235, 0.6)',
                            'rgba(255, 206, 86, 0.6)',
                            'rgba(75, 192, 192, 0.6)',
                            'rgba(153, 102, 255, 0.6)',
                            'rgba(255, 159, 64, 0.6)',
                            'rgba(255, 99, 132, 0.6)'
                        ]
                    }
                ]
            }
        })
    }

    render() {
        return (
            <>
                <Line
                    data={this.state.chartData}
                    width={600}
                    height={200}
                    options={{
                        title: {
                            display: this.props.displayTitle,
                            text: 'Largest Cities in Masachusetts',
                            fontSize: 25,
                            fontColor: '#f4f4f4'

                        },

                        legend: {
                            display: this.props.displayLegend,
                            position: this.props.legendPosition,
                            fontColor: '#f4f4f4',
                            labels: {
                                fontColor: "blue",
                                fontSize: 18
                            }
                        },
                        scales: {
                            yAxes: [{
                                scaleLabel: {
                                    display: true,
                                    labelString: 'probability',
                                    fontColor: '#f4f4f4'
                                }
                            }],
                            xAxes: [{
                                ticks: {
                                    fontColor: '#f4f4f4',
                                    fontSize: 20,
                                    stepSize: 1

                                }
                            }]
                        }
                    }}
                />
            </>
        );
    }
}

export default LineChart;
