import { FaBars, FaTimes } from 'react-icons/fa';
import { MdFingerprint } from 'react-icons/md'
import {  NavLink as Link } from 'react-router-dom';
import styled from 'styled-components';

export const NavBar = styled.div`
    background: #1c2237;
    height: 80px;
    display: flex;
    justify-content: center;
    align-items: center;
    font-size: 1.2rem;
    position: sticky;
    top: 0;
    z-index: 10;
    padding: 0.5rem calc((100vw - 1000px) / 2);
`;

export const NavContainer = styled.div`
    display: flex;
    justify-content: space-between;
    height: 80px;
    z-index: 1;
    width: 100%;
    max-width: 1300px;
    margin-right: auto;
    margin-left: auto;
    padding-right: 50px;
    padding-left: 50px;
`;

export const NavigationLink = styled(Link).attrs(props => {className})`
    & .nav-logo {     
        justify-self: start;
        font-size: 2rem;    
    }

    & .nav-link {

        padding: 0 1rem;
        height: 100%;
    }

    color: #fff;
    text-decoration: none;
    align-items: center;
    display: flex;
    cursor: pointer;
    justify-content:space-between

`;
